import {format} from "date-fns";
import { useEffect } from "react";

export default function Post({title, summary, content, cover, createdAt, author}) {
  useEffect(()=> {
    console.log(author);
  }, [])
  return (
      <div className="post">
        <div className="image">
          <img src="https://images.unsplash.com/photo-1682685797898-6d7587974771?q=80&w=2070&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDF8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D" alt="" />
        </div>
        <div className="texts">
          <h2>{title}</h2>
          <p className="info">
            <a className="author">{author}</a>
            <time>{format(new Date(createdAt), 'd MMM, yyyy HH:mm')}</time>
          </p>
          <p className="summary">{summary}</p>
        </div>
      </div>
    )
}
