import {useEffect} from 'react';
import {useParams} from 'react-router-dom';

export default function PostPage() {
    const {id} = useParams();
    useEffect(() => {
        fetch(`http://localhost:4000/post/${id}`)
    }, [])
    return (
        <>
            Post page
        </>
    );
}
