import {useState} from 'react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import { Navigate } from 'react-router-dom';


export default function EditPost() {
    const [title, setTitle] = useState('');
    const [summary, setSummary] = useState('');
    const [content, setContent] = useState('');
    const [files, setFiles] = useState('');
    const [redirect, setRedirect] = useState('');

    function updatePost(e) {
        e.preventDefault();

    }

    if (redirect) {
        return <Navigate to={'/'} />
      }

    const modules = {
        toolbar: [
          [{ 'header': [1, 2, false] }],
          ['bold', 'italic', 'underline','strike', 'blockquote'],
          [{'list': 'ordered'}, {'list': 'bullet'}, {'indent': '-1'}, {'indent': '+1'}],
          ['link', 'image'],
          ['clean']
        ],
      };
    const formats = [
        'header',
        'bold', 'italic', 'underline', 'strike', 'blockquote',
        'list', 'bullet', 'indent',
        'link', 'image'
      ];
  
      return (
          <form onSubmit={updatePost}>
              <input type="title" placeholder="Titre" value={title} onChange={e => {setTitle(e.target.value)}} />
              <input type="summary" placeholder="Résumé" value={summary} onChange={e => {setSummary(e.target.value)}} />
              <input type="file" onChange={e => {setFiles(e.target.files)}} />
              <Editor onChange={setContent} value={content} />
              {/* <ReactQuill value={content} onChange={newValue => {setContent(newValue)}} modules={modules} formats={formats} /> */}
              <button style={{marginTop:'5px'}}>Créer un post</button>
          </form>
      )
}
